examples.source:
import { Histogram } from '@antv/g2plot';

fetch('https://gw.alipayobjects.com/os/antvdemo/assets/data/diamond.json')
  .then((res) => res.json())
  .then((data) => {
    const histogramPlot = new Histogram('container', {
      data,
      binField: 'depth',
      binWidth: 2,
      stackField: 'cut',
      coloField: 'color',
      tooltip: {
        showMarkers: false,
        position: 'top',
      },
      interactions: [
        {
          type: 'element-highlight',
        },
      ],
    });

    histogramPlot.render();
  });

