import { Pie } from '@antv/g2plot';

const data = [
  { type: '分类一', value: 27 },
  { type: '分类二', value: 25 },
  { type: '分类三', value: 18 },
  { type: '分类四', value: 15 },
  { type: '分类五', value: 10 },
  { type: '其他', value: 5 },
];

const piePlot = new Pie('container', {
  appendPadding: 10,
  data,
  angleField: 'value',
  colorField: 'type',
  radius: 0.8,
  label: {
    type: 'inner',
    // @ts-ignore 偏移 50% TODO 后续支持直接配置 -50%
    offset: '-0.5',
    content: '{name} {percentage}',
    style: {
      fill: '#fff',
      fontSize: 14,
      textAlign: 'center',
    },
  },
  // 设置 状态样式
  state: {
    active: {
      style: {
        lineWidth: 0,
        fillOpacity: 0.65,
      },
    },
  },
  // 添加 element 选中和激活交互
  interactions: [{ type: 'element-selected' }, { type: 'element-active' }],
});

piePlot.render();

//  初始化设置默认状态；状态可叠加，可通过回调设置
piePlot.setState('active', (data) => (data as any).type === '分类一');
piePlot.setState('selected', (data) => (data as any).type === '分类一' || (data as any).type === '分类二');

